import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faPlusCircle, faEdit, faSave, faTimes } from '@fortawesome/free-solid-svg-icons';
import { Tooltip } from 'react-tooltip';
import './index.css';

const UserProfiles = () => {
  const [profiles, setProfiles] = useState([
    { id: 1, name: 'Leila', description: 'Learning', editable: false },
    { id: 2, name: 'Ahmad', description: 'Loves', editable: false },
    { id: 3, name: 'Sara', description: 'Traveling', editable: false },
    { id: 4, name: 'Farid', description: 'Exploring', editable: false },
    { id: 5, name: 'Nadia', description: 'Teaching', editable: false },
    { id: 6, name: 'Dawood', description: 'History', editable: false },
  ]);

  const handleEditToggle = (id) => {
    setProfiles((prevProfiles) =>
      prevProfiles.map((profile) =>
        profile.id === id ? { ...profile, editable: !profile.editable } : profile
      )
    );
  };

  const handleChange = (id, field, value) => {
    setProfiles((prevProfiles) =>
      prevProfiles.map((profile) =>
        profile.id === id ? { ...profile, [field]: value } : profile
      )
    );
  };

  return (
    <div className="user-profiles-page">
      <h1>User Profiles</h1>
      <div className="user-profiles-container">
        {profiles.map((profile) => (
          <div key={profile.id} className="user-profile-card">
            <FontAwesomeIcon icon={faUser} className="user-icon" />
            <div className="user-info">
              {profile.editable ? (
                <>
                  <input
                    type="text"
                    value={profile.name}
                    onChange={(e) => handleChange(profile.id, 'name', e.target.value)}
                  />
                  <textarea
                    value={profile.description}
                    onChange={(e) => handleChange(profile.id, 'description', e.target.value)}
                  />
                </>
              ) : (
                <>
                  <h2>Name: {profile.name}</h2>
                  <p>Description: {profile.description}</p>
                </>
              )}
            </div>
            <div className="profile-actions">
              <button
                onClick={() => handleEditToggle(profile.id)}
                className={profile.editable ? 'save-btn' : 'edit-btn'}
                data-tooltip-id={`edit-save-tooltip-${profile.id}`}
              >
                <FontAwesomeIcon icon={profile.editable ? faSave : faEdit} />
                {profile.editable ? ' Save' : ' Edit'}
              </button>
              <Tooltip id={`edit-save-tooltip-${profile.id}`} place="top" effect="solid">
                {profile.editable ? 'Save changes' : 'Edit profile'}
              </Tooltip>
              {profile.editable && (
                <button
                  onClick={() => handleEditToggle(profile.id)}
                  className="cancel-btn"
                  data-tooltip-id={`cancel-tooltip-${profile.id}`}
                >
                  <FontAwesomeIcon icon={faTimes} /> Cancel
                </button>
              )}
              {profile.editable && (
                <Tooltip id={`cancel-tooltip-${profile.id}`} place="top" effect="solid">
                  Cancel changes
                </Tooltip>
              )}
              {!profile.editable && (
                <button className="profile-button">
                  <FontAwesomeIcon icon={faPlusCircle} /> Follow
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserProfiles;
