import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faCaretDown } from '@fortawesome/free-solid-svg-icons';
import './index.css';
import { Link, useHistory } from 'react-router-dom';

const Navbar = () => {
  const [lessonsOpen, setLessonsOpen] = useState(false);
  const [communityOpen, setCommunityOpen] = useState(false);
  const [searchField, setSearchField] = useState('');
  const history = useHistory();

  const toggleLessons = () => {
    setLessonsOpen(!lessonsOpen);
  };

  const toggleCommunity = () => {
    setCommunityOpen(!communityOpen);
  };

  const handleSearchChange = (e) => {
    setSearchField(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchField.trim()) {
      history.push(`/search?query=${searchField}`);
    } else {
      alert('Please enter a search term');
    }
  };

  return (
    <nav className="navbar">
      <h1>BE DAMASCENE</h1>
      <div className="links">
        <Link to="/">Home</Link>
        <div className="dropdown">
          <button onClick={toggleLessons}>
            Lessons <FontAwesomeIcon icon={faCaretDown} />
          </button>
          {lessonsOpen && (
            <div className="dropdown-content">
              <Link to="/lessons/beginner">Beginner</Link>
              <Link to="/lessons/intermediate">Intermediate</Link>
              <Link to="/lessons/advanced">Advanced</Link>
            </div>
          )}
        </div>
        <div className="dropdown">
          <button onClick={toggleCommunity}>
            Community <FontAwesomeIcon icon={faCaretDown} />
          </button>
          {communityOpen && (
            <div className="dropdown-content">
              <Link to="/community/chat">Chat</Link>
              <Link to="/community/forum">Forum</Link>
              <Link to="/community/profiles">User Profiles</Link>
            </div>
          )}
        </div>
        <Link to="/culture">Culture</Link>
        <Link to="/account">Account</Link>
        <Link to="/contact">Contact Us</Link>
      </div>
      <div className="search-box">
        <form onSubmit={handleSearchSubmit}>
          <input
            type="search"
            placeholder="Search..."
            value={searchField}
            onChange={handleSearchChange}
            className="search-input"
          />
          <button type="submit" className="search-icon-btn">
            <FontAwesomeIcon icon={faSearch} className="search-icon" />
          </button>
        </form>
      </div>
    </nav>
  );
};

export default Navbar;
